import React from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import SimpleMDE from 'react-simplemde-editor';
import axios from '../../axios';

import 'easymde/dist/easymde.min.css';
import styles from './AddPost.module.scss';

export const AddPost = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const inputFileRef = React.useRef(null);
    const [isLoading, setIsLoading] = React.useState(false);
    const [fields, setFields] = React.useState({
        title: '',
        text: '',
        imageUrl: '',
        address: '',
    });

    const isEditing = Boolean(id);

    React.useEffect(() => {
        if (id) {
            axios
                .get(`/posts/${id}`)
                .then(({ data }) => {
                    console.log('Fetched post data:', data);
                    setFields(data);
                })
                .catch((err) => {
                    console.warn('Error fetching post:', err);
                    alert('Ошибка при получении статьи');
                });
        }
    }, []);

    const isEmptyFields = Object.values({
        title: fields.title,
        text: fields.text,
        address: fields.address,
    }).some((v) => !v);

    const setFieldValue = (name, value) => {
        setFields((prev) => ({ ...prev, [name]: value }));
    };

    const onChange = React.useCallback((value) => {
        setFieldValue('text', value);
    }, []);

    const options = React.useMemo(
        () => ({
            spellChecker: false,
            maxHeight: '400px',
            autofocus: true,
            placeholder: 'Введите текст...',
            status: false,
            autosave: {
                enabled: true,
                delay: 1000,
            },
        }),
        [],
    );

    const handleChangeFile = async (e) => {
        try {
            const formData = new FormData();
            const file = e.target.files[0];

            if (!file) return;

            formData.append('image', file);
            const { data } = await axios.post('/upload', formData);

            // Store only the URL path without the base URL
            setFieldValue('imageUrl', data.url);
            e.target.value = '';
        } catch (e) {
            console.warn(e);
            alert('Ошибка при загрузке файла');
        }
    };

    const onSubmit = async () => {
        try {
            setIsLoading(true);
            console.log('Submitting fields:', fields);

            const apiMethod = isEditing
                ? axios.patch.bind(this, `/posts/${id}`)
                : axios.post.bind(this, '/posts');

            // Ensure imageUrl starts with /uploads/
            const imageUrl = fields.imageUrl?.startsWith('/uploads/')
                ? fields.imageUrl
                : fields.imageUrl?.startsWith('http://localhost:4444/uploads/')
                ? fields.imageUrl.replace('http://localhost:4444', '')
                : `/uploads/${fields.imageUrl}`;

            const postData = {
                title: fields.title,
                text: fields.text,
                imageUrl: imageUrl,
                address: fields.address,
            };

            console.log('Sending data:', postData);

            const { data } = await apiMethod(postData);
            console.log('Response:', data);

            const postId = id || data._id;

            navigate(`/posts/${postId}`);
        } catch (err) {
            console.warn('Error saving post:', err);
            if (err.response) {
                console.log('Error response:', err.response.data);
                alert(`Ошибка при сохранении статьи: ${err.response.data.message || 'Неизвестная ошибка'}`);
            } else {
                alert('Ошибка при сохранении статьи');
            }
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <Paper style={{ padding: 30 }}>
            <Button onClick={() => inputFileRef.current.click()} variant="outlined" size="large">
                Загрузить превью
            </Button>
            <input ref={inputFileRef} hidden type="file" onChange={handleChangeFile} />
            {fields.imageUrl && (
                <Button
                    variant="contained"
                    color="error"
                    onClick={() => setFieldValue('imageUrl', '')}>
                    Удалить
                </Button>
            )}
            {fields.imageUrl && (
                <img className={styles.image} src={`http://localhost:4444${fields.imageUrl}`} alt="" />
            )}
            <br />
            <br />
            <TextField
                value={fields.title}
                onChange={(e) => setFieldValue('title', e.target.value)}
                classes={{ root: styles.title }}
                variant="standard"
                placeholder="Заголовок статьи..."
                fullWidth
            />
            <TextField
                value={fields.address}
                onChange={(e) => setFieldValue('address', e.target.value)}
                classes={{ root: styles.address }}
                variant="standard"
                placeholder="Адрес"
                fullWidth
            />
            <SimpleMDE
                className={styles.editor}
                value={fields.text}
                onChange={onChange}
                options={options}
            />
            <div className={styles.buttons}>
                <Button onClick={onSubmit} disabled={isEmptyFields} size="large" variant="contained">
                    {isEditing ? 'Сохранить' : 'Опубликовать'}
                </Button>
                <Link to="/">
                    <Button disabled={isLoading} size="large">
                        Отмена
                    </Button>
                </Link>
            </div>
        </Paper>
    );
};
