import React from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import SimpleMDE from 'react-simplemde-editor';
import axios from '../../axios';

import 'easymde/dist/easymde.min.css';
import styles from './AddPost.module.scss';

export const AddPost = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const inputFileRef = React.useRef(null);
    const [isLoading, setIsLoading] = React.useState(false);
    const [fields, setFields] = React.useState({
        title: '',
        text: '',
        imageUrl: '',
        address: '',
    });

    const isEditing = Boolean(id);

    React.useEffect(() => {
        if (id) {
            axios
                .get(`/posts/${id}`)
                .then(({ data }) => {
                    console.log('Fetched post data:', data);
                    setFields(data);
                })
                .catch((err) => {
                    console.warn('Error fetching post:', err);
                    alert('Ошибка при получении статьи');
                });
        }
    }, []);

    const isEmptyFields = Object.values({
        title: fields.title,
        text: fields.text,
        address: fields.address,
        threeQuote: fields.threeQuote,
    }).some((v) => !v);

    const setFieldValue = (name, value) => {
        setFields((prev) => ({ ...prev, [name]: value }));
    };

    const onChange = React.useCallback((e) => {
        const value = e.target ? e.target.value : e;
        setFieldValue('text', value);
    }, []);

    const options = React.useMemo(
        () => ({
            spellChecker: false,
            maxHeight: '400px',
            autofocus: true,
            placeholder: 'Введите текст...',
            status: false,
            autosave: {
                enabled: true,
                delay: 1000,
            },
        }),
        []
    );

    const handleChangeFile = async (e) => {
        try {
            const formData = new FormData();
            const file = e.target.files[0];

            if (!file) return;

            formData.append('image', file);
            const { data } = await axios.post('/upload', formData);

            // Store the URL as returned by the server
            const imageUrl = data.url.startsWith('/') ? data.url : `/${data.url}`;
            setFieldValue('imageUrl', imageUrl);
            e.target.value = '';
        } catch (e) {
            console.warn('Upload error:', e.response?.data || e);
            alert('Ошибка при загрузке файла');
        }
    };

    const onSubmit = async () => {
        try {
            setIsLoading(true);
            console.log('Submitting fields:', fields);

            const apiMethod = isEditing
                ? axios.patch.bind(this, `/posts/${id}`)
                : axios.post.bind(this, '/posts');

            // Handle external URLs vs uploaded images
            const imageUrl = fields.imageUrl?.startsWith('http')
                ? fields.imageUrl // Keep external URLs as is
                : fields.imageUrl?.startsWith('/uploads/')
                ? fields.imageUrl // Keep upload path as is
                : `/uploads/${fields.imageUrl}`; // Add uploads prefix if needed

            const postData = {
                title: fields.title,
                text: fields.text,
                imageUrl: imageUrl,
                address: fields.address,
                threeQuote: fields.threeQuote,
            };

            console.log('Sending data:', postData);

            const { data } = await apiMethod(postData);
            console.log('Response:', data);

            const postId = id || data._id;

            navigate(`/posts/${postId}`);
        } catch (err) {
            console.warn('Error saving post:', err.response?.data || err);
            if (err.response?.data?.message) {
                alert(`Ошибка: ${err.response.data.message}`);
            } else {
                alert('Ошибка при сохранении статьи');
            }
        } finally {
            setIsLoading(false);
        }
    };

    const validateThreeQuote = (value) => {
        const words = value.split('');
        return words.length !== 3 ? value : words.slice(0, 3).join(' ');
    };

    return (
        <div className={styles.addpost}>
            <div className="container">
                {fields.imageUrl && (
                    <img
                        className={styles.image}
                        src={
                            fields.imageUrl?.startsWith('http')
                                ? fields.imageUrl
                                : `http://localhost:4444${fields.imageUrl}`
                        }
                        alt=""
                    />
                )}
                <button onClick={() => inputFileRef.current.click()} className={styles.button}>
                    Загрузить превью
                </button>
                <input ref={inputFileRef} hidden type="file" onChange={handleChangeFile} />
                {fields.imageUrl && (
                    <button
                        className={`${styles.button} ${styles.buttonError}`}
                        onClick={() => setFieldValue('imageUrl', '')}
                    >
                        Удалить
                    </button>
                )}

                <br />
                <br />
                <input
                    type="text"
                    value={fields.title}
                    onChange={(e) => setFieldValue('title', e.target.value)}
                    className={`${styles.input} ${styles.title}`}
                    placeholder="Заголовок статьи..."
                />
                <input
                    type="text"
                    value={fields.address}
                    onChange={(e) => setFieldValue('address', e.target.value)}
                    className={`${styles.input} ${styles.address}`}
                    placeholder="Адрес"
                />
                <input
                    type="text"
                    value={fields.threeQuote}
                    onChange={(e) => {
                        const validValue = validateThreeQuote(e.target.value);
                        setFieldValue('threeQuote', validValue);
                    }}
                    className={`${styles.input} ${styles.threeQuote}`}
                    placeholder="Введите ровно 3 слова"
                />
                <textarea
                    className={styles.editor}
                    value={typeof fields.text === 'object' ? JSON.stringify(fields.text) : fields.text}
                    onChange={onChange}
                    options={options}
                />
                <div className={styles.buttons}>
                    <button
                        onClick={onSubmit}
                        disabled={isEmptyFields}
                        className={`${styles.button} ${styles.buttonContained}`}
                    >
                        {isEditing ? 'Сохранить' : 'Опубликовать'}
                    </button>
                    <Link to="/">
                        <button disabled={isLoading} className={styles.button}>
                            Отмена
                        </button>
                    </Link>
                </div>
            </div>
        </div>
    );
};
