import React from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import axios from 'axios';

import styles from './AddPost.module.scss';

export const AddPost = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const inputFileRef = React.useRef(null);
    const [isLoading, setIsLoading] = React.useState(false);
    const [fields, setFields] = React.useState({
        title: '',
        text: '',
        imageUrl: '',
        address: '',
    });

    const isEditing = Boolean(id);

    React.useEffect(() => {
        if (id) {
            axios
                .get(`/posts/${id}`)
                .then(({ data }) => {
                    setFields(data);
                })
                .catch((err) => {
                    alert('Ошибка при получении статьи');
                    console.warn(err);
                });
        }
    }, []);

    const isEmptyFields = Object.values({title:fields.title, text:fields.text, address:fields.address}).some((v) => !v);

    const setFieldValue = (name, value) => {
        setFields(prev => ({...prev, [name]: value}));
    };

    const onChange = React.useCallback((value) => {
        setFieldValue('text', value)
    }, []);

    const options = React.useMemo(() => {
        return {
            spellChecker: false,
            maxHeight: '400px',
            autofocus: true,
            placeholder: 'Введите текст...',
            status: false,
            autosave: {
                enabled: true,
                delay: 1000,
            },
        };
    }, []);

    const handleChangeFile = async (e) => {
        try {
            const formData = new FormData()
            formData.append('image', e.target.files[0])
            const { data } = await axios.post('/upload', formData)
            setFieldValue('imageUrl', data.url)
            e.target.value = ''
        } catch (e) {
            console.warn(e)
            alert('Ошибка при загрузке файла')
        }};

    const onSubmit = async () => {
        try {
            setIsLoading(true);
            console.log(fields);

            const apiMethod = isEditing ? 
            axios.patch.bind(this, `/posts/${id}`) : 
            axios.post.bind(this, '/posts');

            const {data} = await apiMethod({
                title: fields.title,
                text: fields.text,
                imageUrl: fields.imageUrl,
                address: fields.address,
            });

            const postId = id || data._id;

            navigate(`/posts/${postId}`);
        } catch (err) {
            console.log(err);
        } finally {
            setIsLoading(false)
        }
    };

    return (
        <>
            <button onClick={() => inputFileRef.current.click()}>Загрузить превью</button>
            <input ref={inputFileRef} type="file" onChange={handleChangeFile} />
            {fields.imageUrl && (
                <button onClick={() => setFieldValue('imageUrl', '')}>Удалить</button>
            )}
            {fields.imageUrl && (
                <img
                    className={styles.image}
                    src={`http://localhost:4444${fields.imageUrl}`}
                    alt="Uploaded"
                />
            )}

            <br />
            <br />

            <input
                value={fields.title}xx                placeholder="Заголовок статьи"
            />
            <input
                value={fields.address}
x                placeholder="Адрес"
            />
            <textarea
                value={fields.text}
                onChange={onChange}
                placeholder="Текст статьи"
            />
            <button onClick={onSubmit} disabled={isEmptyFields}>
                {isEditing ? 'Опубликовать' : 'Сохранить'}
            </button>
        </>
    );
};
