import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import clsx from "clsx";
import axios from "../../axios";

import styles from "./Post.module.scss";

const [phone, setPhone] = useState("");
export const Post = ({
    _id,
    title,
    imageUrl,
    address,
    createdAt,
    updatedAt,
    isAdmin,
    text,
    isLoading,
    isFullPost,
}) => {
    if (isLoading) {
        return "";
    }


    const handlePhoneChange = (e) => {
        let value = e.target.value.replace(/\D/g, ""); // Удаляем все нецифровые символы
        let formattedValue = "+7 (";

        if (value.length > 1) {
            formattedValue += value.substring(1, 4);
        }
        if (value.length >= 5) {
            formattedValue += ") " + value.substring(4, 7);
        }
        if (value.length >= 8) {
            formattedValue += "-" + value.substring(7, 9);
        }
        if (value.length >= 10) {
            formattedValue += "-" + value.substring(9, 11);
        }

        setPhone(formattedValue);
    };

    return (
        <>
            {isFullPost ? (
                <div className={styles.fullPost}>
                    <div className={styles.main_img}>
                        <img src={imageUrl} alt="" />
                        <div className="container">
                            <div className="container_1200">
                                <div className={styles.desc}>
                                    <div className="path_site">
                                        <Link to={"/"} className="path_to">
                                            Главная
                                        </Link>
                                        <Link to={"/"} className="path_to">
                                            Проекты
                                        </Link>
                                        <p className="path_to">{title}</p>
                                    </div>
                                    <h3>{title}</h3>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="container container_1200">
                        <div className={styles.name_desc}>
                            <h3>{title}</h3>
                            <p>{text}</p>
                        </div>
                    </div>

                    <div className={styles.order_consultation}>
                        <div className="container">
                            <div className="container_1200">
                                <div className={styles.desc_form}>
                                    <div className={styles.desc}>
                                        <h3>Закажите консультацию</h3>
                                        <p>
                                            Оставьте заявку, и мы свяжемся с
                                            вами в ближайшее время
                                        </p>
                                    </div>

                                    <form>
                                        <input
                                            type="text"
                                            name="first_name"
                                            id="first_name"
                                            placeholder="Ваше имя"
                                        />
                                        <input
                                            type="tel"
                                            id="phone"
                                            name="phone"
                                            placeholder="Ваш номер телефона"
                                            value={phone}
                                            onChange={handlePhoneChange}
                                            maxLength="18"
                                            required
                                        />
                                        <button type="submit">
                                            <p>Отправить заявку</p>
                                            <p>→</p>
                                        </button>
                                        <p>
                                            Нажимая на кнопку, Вы подтверждаете,
                                            что ознакомились с{" "}
                                            <span className="green_underline">
                                                <a href="">
                                                    Политикой конфиденциальности
                                                </a>
                                            </span>{" "}
                                            компании и даете свое{" "}
                                            <span className="green_underline">
                                                <a href="">
                                                    согласие на обработку
                                                    персональных данных
                                                </a>
                                            </span>{" "}
                                            и{" "}
                                            <span className="green_underline">
                                                <a href="">
                                                    согласие на обработку
                                                    персональных данных для
                                                    распространения
                                                </a>
                                            </span>
                                            .
                                        </p>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            ) : (
                <div className={styles.complexe}>
                    <Link to={`/posts/${_id}`}>
                        {imageUrl && <img src={imageUrl} alt={title} />}
                        <div className={styles.desc_statuses}>
                            <div className={styles.statuses}>
                                <p>видеообзор</p>
                                <p>online</p>
                            </div>
                            <div className={styles.desc}>
                                <h3>{title}</h3>
                                <p>{address}</p>
                            </div>
                        </div>
                    </Link>
                </div>
            )}
        </>
    );
};
