import { React, useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import axios from '../../axios';
import { useForm } from 'react-hook-form';

import styles from './Login.module.scss';
import { setUserData } from '../../redux/slices/auth';

export const Login = () => {
    const [isLogin, setIsLogin] = useState(true);

    const dispatch = useDispatch();
    const navigate = useNavigate();
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();

    const onSubmit = async (fields) => {
        try {
            const { data } = await axios.post('/auth/login', fields);
            dispatch(setUserData(data));
            window.localStorage.setItem('token', data.token);
            navigate('/');
        } catch (error) {
            console.log(error);
            if (error.isAxiosError) {
                if (error.response.data.errors) {
                    error.response.data.errors.forEach((obj) => {
                        setError(
                            obj.param,
                            {
                                message: obj.msg,
                            },
                            { shouldFocus: true }
                        );
                    });
                }
                if (error.response.data.message) {
                    setError(
                        'password',
                        { message: error.response.data.message },
                        { shouldFocus: true }
                    );
                }
            }
        }
    };

    return (
        <>
            <div className={styles.auth}>
                <div className={styles.choice}>
                    <h4
                        onClick={() => {
                            setIsLogin(true);
                        }}
                        className={isLogin ? '' : styles.choice_active}
                    >
                        Авторизация
                    </h4>
                    <h4
                        onClick={() => {
                            setIsLogin(false);
                        }}
                        className={isLogin ? styles.choice_active : ''}
                    >
                        Регистрация
                    </h4>
                    <hr />
                </div>

                {isLogin ? (
                    <form action="" autocomplete="off" key="login">
                        <input type="email" placeholder="Почта" autocomplete="off" />
                        <input
                            type="password"
                            name="password"
                            id="1"
                            placeholder="Пароль"
                            autocomplete="off"
                        />
                        <input type="submit" value="ВОЙТИ" />
                    </form>
                ) : (
                    <form action="" autocomplete="off" key="register">
                        <input type="text" placeholder="Пользователь" autocomplete="off" />
                        <input type="email" placeholder="Почта" autocomplete="off" />
                        <input
                            type="password"
                            name="password"
                            id="1"
                            placeholder="Пароль"
                            autocomplete="off"
                        />
                        <input
                            type="password"
                            name="password2"
                            id="2"
                            placeholder="Подтвердите пароль"
                            autocomplete="off"
                        />
                        <input type="submit" value="ЗАРЕГЕСТРИРОВАТЬСЯ" />
                    </form>
                )}
            </div>
        </>
    );
};
